export declare const TodoListEditorStyle = "\n  .rek-todo-list {\n    display:flex;\n    flex-direction:column;\n  }  \n\n  .rek-todo-item {\n    display:flex;\n    align-items:center;\n    margin: 4px 0;    \n    border-radius:3px;\n    padding:4px;\n      \n    .rek-check-container {\n      margin-bottom:15px;\n      transform:scale(.8);\n    }    \n\n    .rek-todo-item-content {\n      flex-grow:1;\n    }\n  }\n  .rek-todo-item-content.rek-empty:before {\n    content: attr(placeholder);\n    position:absolute;\n    opacity:0.2;\n    display: block; /* For Firefox */\n  }\n\n  .rek-todo-item[data-todo-complete=true] {\n    text-decoration: line-through;\n  }\n\n  .rek-todo-item.focus {\n    background-color:var(--gray-light2-color);\n  }\n\n  .rek-todo-item-due-date {\n    display:flex;\n    align-items:center;\n    padding:0 8px;\n  }\n\n";
