export declare const EditorToolbarGlobalStyle = "\n\n  .rek-editor-toolbar-wrapper {\n    display: flex;\n    align-items: center;\n    flex-shrink:0;\n    width:100%;\n    background-color: var(--content-background);\n    overflow:hidden;\n\n    * {\n      flex-shrink:0;\n      box-sizing: border-box;\n    }    \n  }\n\n  .rek-editor-toolbar-wrapper.rek-no-overflow {\n    .more-icon {\n      display:none;\n    }\n  }\n\n  .rek-editor-toolbar-overflow {\n    cursor:pointer;\n  }\n\n  .rek-editor-toolbar {\n    display: flex;    \n    align-items: center;\n    justify-content:center;\n    border-radius: 3px;\n    padding: 4px;    \n    flex-grow:1;\n    \n    .rek-icon-button {\n      min-width:36px;  \n      width:36px; \n      flex-shrink:0;\n    }\n    \n    .rek-v-toolbar-divider:last-child,\n    .rek-v-toolbar-divider:first-child {\n      display:none;\n    }    \n  }  \n\n  .rek-floating-content {\n    .rek-editor-toolbar-wrapper {\n      flex-grow:0;\n    }\n    .rek-editor-toolbar {\n      flex-grow:0;      \n    }\n  }\n  \n  \n";
